import { AnonCredsRsModule } from '@aries-framework/anoncreds-rs'
import { AskarModule } from '@aries-framework/askar'
import { agentDependencies } from '@aries-framework/react-native'
import {
  Agent,
  AutoAcceptCredential,
  AutoAcceptProof,
  ConnectionsModule,
  ConsoleLogger,
  CredentialsModule,
  DidsModule,
  HttpOutboundTransport,
  KeyDidRegistrar,
  KeyDidResolver,
  LogLevel,
  ProofsModule,
  V2CredentialProtocol,
  V2ProofProtocol,
  WebDidResolver,
  WsOutboundTransport,
  utils,
  RecipientModule,
} from '@aries-framework/core'
import {
  AnonCredsModule,
  AnonCredsCredentialFormatService,
  AnonCredsProofFormatService,
} from '@aries-framework/anoncreds'

import { ariesAskar } from '@hyperledger/aries-askar-react-native'
import { anoncreds } from '@hyperledger/anoncreds-react-native'
import { CheqdAnonCredsRegistry, CheqdDidRegistrar, CheqdDidResolver } from '@aries-framework/cheqd'

export const agent = new Agent({
  config: {
    label: 'Demo Agent',
    autoUpdateStorageOnStartup: false,
    walletConfig: {
      id: `demo-agent-${utils.uuid()}`,
      key: 'demo-agent-key',
    },
    // Change to view logs in terminal
    logger: new ConsoleLogger(LogLevel.debug),
  },
  modules: {
    // Storage
    askar: new AskarModule({
      ariesAskar,
    }),

    mediationRecipient: new RecipientModule({
      mediatorInvitationUrl:
        'https://mediator.dev.animo.id/invite?',
    }),

    // Connections module is enabled by default, but we can
    // override the default configuration
    connections: new ConnectionsModule({
      autoAcceptConnections: true,
    }),

    // Credentials module is enabled by default, but we can
    // override the default configuration
    credentials: new CredentialsModule({
      autoAcceptCredentials: AutoAcceptCredential.Always,

      // Support v2 protocol
      credentialProtocols: [
        new V2CredentialProtocol({
          credentialFormats: [new AnonCredsCredentialFormatService()],
        }),
      ],
    }),

    // Proofs module is enabled by default, but we can
    // override the default configuration
    proofs: new ProofsModule({
      autoAcceptProofs: AutoAcceptProof.Always,

      // Support v2 protocol
      proofProtocols: [
        new V2ProofProtocol({
          proofFormats: [new AnonCredsProofFormatService()],
        }),
      ],
    }),

    // Dids
    dids: new DidsModule({
      // Support creation of did:key dids
      // FIXME: add cheqd did resolver
      registrars: [new KeyDidRegistrar(), new CheqdDidRegistrar()],
      // Support resolving of did:indy, did:sov, did:key and did:web dids
      // FIXME: add cheqd did registrar
      resolvers: [new KeyDidResolver(), new WebDidResolver(), new CheqdDidResolver()],
    }),

    // AnonCreds
    anoncreds: new AnonCredsModule({
      // Support indy anoncreds method
      // FIXME: add cheqd registry
      registries: [new CheqdAnonCredsRegistry()],
    }),
    // Use anoncreds-rs as anoncreds backend
    _anoncreds: new AnonCredsRsModule({
      anoncreds,
    }),

    // FIXME: add cheqd module
  },
  dependencies: agentDependencies,
})

agent.registerOutboundTransport(new HttpOutboundTransport())
agent.registerOutboundTransport(new WsOutboundTransport())
